import React, { Component, useState } from 'react';


class MainForm extends Component {

    state={
        title: '',
        deadline: '',
        status: ''
    }

    userInput (type, value) {
        let stateCopy = {...this.state};
        let dataType = stateCopy[type];
        dataType = value;

        this.setState({ ...stateCopy });
    }

    render () {


        return (
            <div className='form'>
                <h3>Add new todo</h3>
                <form onSubmit={this.props.submission}>
                    <input placeholder='Title' name='title' onChange={(event) => this.userInput('title', event.target.value)}/>
                    <input placeholder='Deadline' name='deadline' onChange={(event) => this.userInput('deadline', event.target.value)}/>
                    <select defaultValue="Status" onChange={(event) => this.userInput('status', event.target.value)}>
                        <option value="Done">Done</option>
                        <option value="Not Started">Not started</option>
                        <option value="In progress">In progress</option>
                    </select>

                    <div className='formButton'>
                        <button>Cancel</button>
                        <button onClick={(value) => this.props.submitForm(this.state)}>Add</button>
                    </div>
                </form>
            </div>
        )
    }

}

export default MainForm;
