import React, { useState } from 'react';


const MainForm = (props) => {

    const [title, setTitle] = useState();

    const [deadline, setDeadline] = useState();

    const [status, setStatus] = useState();

    console.log(title, deadline, status);


    return (
        <div className='form'>
            <h3>Add new todo</h3>
            <form onSubmit={props.submission}>
                <input placeholder='Title' name='title' onChange={(event) => setTitle(event.target.value)}/>
                <input placeholder='Deadline' name='deadline' onChange={(event) => setDeadline(event.target.value)}/>
                <select defaultValue="Status" onChange={(event) => setStatus(event.target.value)}>
                    <option value="Done">Done</option>
                    <option value="Not Started">Not started</option>
                    <option value="In progress">In progress</option>
                </select>

                <div className='formButton'>
                    <button>Cancel</button>
                    <button onClick={() => props.submitForm({title: title, deadline: deadline, status: status})}>Add</button>
                </div>
            </form>
        </div>
    )
}

export default MainForm;
